var xss = require('../lib/xss'),
    assert = require('assert');

module.exports = {
    'test #xss()': function () {
        //Need more tests!
        assert.equal(' foobar', xss.clean('javascript  : foobar'));
        assert.equal(' foobar', xss.clean('j a vasc ri pt: foobar'));
        assert.equal('<a >some text</a>', xss.clean('<a href="javascript:alert(\'xss\')">some text</a>'));

        var url = 'http://www.example.com/test.php?a=b&b=c&c=d';
        assert.equal(url, xss.clean(url));
    }

}
